mapper.update:
update booking_items
set booking_id = #{bookingId,jdbcType=OTHER,typeHandler=com.ds.ticketmaster.config.database.UUIDTypeHandler},
  seat_inventory_id = #{seatInventoryId,jdbcType=OTHER,typeHandler=com.ds.ticketmaster.config.database.UUIDTypeHandler},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=OTHER,typeHandler=com.ds.ticketmaster.config.database.UUIDTypeHandler}

mapper.select:
select id, booking_id, seat_inventory_id, created_at, updated_at
from booking_items
where id = #{id,jdbcType=OTHER,typeHandler=com.ds.ticketmaster.config.database.UUIDTypeHandler}
